import { ADD_FAV, MAP_FAV, REMOVE_FAV} from "./action-types";

export const addFav = (character) => {
    return {
        type: ADD_FAV,
        payLoad: character
    }
}

export const removeFav = (id) => {
    return {
        type: REMOVE_FAV,
        payLoad: id
    }
}

export const mapFav = (value) => {
    return {
        type: MAP_FAV,
        payLoad: value
    }
}

export const getCharacters = () => {
    return async function(dispatch, input){
        const response = await fetch(`https://rickandmortyapi.com/api/character${input}`)
    }
}
