import style from "./Detail.module.css";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

function Detail(props){

    const {idParam} = useParams()
    const [character, setCharacter] = useState({})

    useEffect(() =>{
        fetch(`https://rickandmortyapi.com/api/character/${idParam}`)
        .then((response) => response.json)
        .then((data) =>{
            data.name ? setCharacter(data): window.alert("No existe un personaje con ese ID")
        })
    
    },[idParam])

    const { id, name, species, gender, image } = character
    
    return(
        <div className={style.Container}>
            <div className={style.ButtonContainer}>
            </div>
            
                <div className={style.Card}>
                    <div className={style.Id}>
                        {id}
                    </div>
                    <div className={style.Heart}>   
                    </div>
                    <img className={style.Image} src={image} alt={image} />
                    <div className={style.Atributes}>
                        <h2>Name: {name}</h2>
                        <h2>Specie:  {species}</h2>
                        <h2>Gender: {gender}</h2>
                    </div>
                </div>
        </div>
)
}

export default Detail
