import "./App.css";
import SearchBar from "./components/SearchBar/SearchBar";
import Card from "./components/Card/Card";
import Nav from "./components/Nav/Nav";
import Cards from "./components/Cards/Cards";
import { useState } from "react";
import { Routes, Route, BrowserRouter } from "react-router-dom";

function App() {
  const [characters, setCharacters] = useState([]);

  const onSearch = (input) => {
    fetch(`https://rickandmortyapi.com/api/character/${input}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.name) {
          if (characters.some((character) => character.id === data.id)) {
            window.alert("This character already exists");
          } else {
            setCharacters([data, ...characters]);
          }
        } else window.alert("The character doesn't exists");
      });
  };

  const handleDelete = (id) => {
    setCharacters(characters.filter((character) => character.id !== id));
  };

  return (


      <Routes>
        <Nav onSearch={onSearch} />
        <Route path="home" element = {<div> <Cards characters={characters} onDelete={handleDelete} /> </div>} />
      </Routes>



  );
}

export default App;
