const emailValidation = (errors, name, value) => {
    if(!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)){
        return {...errors, [name]: "este no es un correo valido"}
    }
    return {...errors}
}

export default emailValidation;
