import { createStore, applyMiddleware, compose } from "redux";
import reducer from "./reducer";
import { ThunkMiddleware } from "redux-thunk";

const composeEnhancer = window._REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose; // Redux Devtools

const store = createStore(
    reducer,
    composeEnhancer(applyMiddleware(ThunkMiddleware)) // es para hacer llamadas async
)


export default store;
