import { ADD_FAV } from "./action-types";

const initialState = {
    myFavorites: [],
}

const reducer = (state = initialState, action ) => {
    switch(action.type){
        case ADD_FAV:
            return {
                ...state,
                myFavorites:[...state.myFavorites]
            }


        default:
            return {...state}
    }
}

export default reducer;
